package Javaprograms;

public class switchcaseprg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 char op='+';
 int x=10;
 int y=20;
 switch(op){
 case '+':
	 System.out.println(x+y);
	 break;
 case '*':
	 System.out.println(x*y);
	 break;
 case '-':
	 System.out.println(x-y);
	 break;
default:
	System.out.println("invalid op");

	 
	 
 }
	}

}
